import React from "react";
import iphone13 from "../Assets/iphone.png";
import iphone13mini from "../Assets/iphone13mini.png";
import iphone13pro from "../Assets/iphone13pro.png";

class Home extends React.Component {
  cards = [
    {
      id: 1,
      title: "iPhone 13 Mini",
      image: iphone13mini,
      description: "Iphone 13 mini, Smallest powerful phone",
    },
    {
      id: 2,
      title: "iPhone 13 Pro ",
      image: iphone13,
      description: "Iphone 13, Phone of the Decade",
    },
    {
      id: 3,
      title: "iPhone 13 Pro Max",
      image: iphone13pro,
      description: "Iphone 13 Pro, Best of the Best",
    },
  ];
  constructor(props) {
    super(props);
    this.state = {
      butttonText: "Red",
    };
  }

  render() {
    return (
      <div className="wrapper">
        {this.cards.map((card, index) => {
          return (
            <Card
              key={index}
              img={card.image}
              title={card.title}
              description={card.description}
            />
          );
        })}
      </div>
    );
  }
}

function Card(props) {
  return (
    <div className="card">
      <div className="card_body">
        <img src={props.img} alt="Iphone" class="card_image" />
        <h2 className="card_title">{props.title}</h2>
        <p className="card_description">{props.description}</p>
        <button className="card_btn"> Buy Phone</button>
      </div>
    </div>
  );
}
export default Home;
